by_date_created.map:
function(doc) { 
  if(doc.data_type== 'session-event') emit(doc.date_created, { _id:doc._id, data_type:doc.data_type, date_created:doc.date_created, user_id:doc.user_id, ip:doc.ip, action_result:doc.action_result }); 
 }

